import React, { useState, useEffect } from 'react'
import Axios from 'axios'
import '../../style/default.scss'
import { Card, Form, Button } from 'react-bootstrap'
import '../../style/member/member_content.scss'
import { MdVisibility, MdDone } from 'react-icons/md'
import { withRouter, Link } from 'react-router-dom'

function MemberContent(props) {
  const [member_name, setMembername] = useState('')
  const [member_account, setMemberaccount] = useState('')
  const [member, setMember] = useState([])
  const [newaccount, setNewMemberaccount] = useState('')

  useEffect(() => {
    Axios.get(`http://localhost:3001/api/get/${props.match.params.id}`).then(
      (res) => {
        setMember(res.data)
      }
    )
  })

  const UpdateReview = () => {
    Axios.put('http://localhost:3001/api/update', {
      member_name: member_name,
      member_account: newaccount,
    })
    setNewMemberaccount('')
  }

  return (
    <>
      <div className="container ">
        <Card className="mem_box">
          <Card.Header className="mem_title d-flex justify-content-between">
            <h5>會員資料</h5>
            <Link to="/see">
              <Button onclick="" className="btn-style botton-font btn_icon">
                <MdVisibility />
                檢視
              </Button>
            </Link>
          </Card.Header>
          <Card.Body style={{ padding: '0  38px  43px 42px' }}>
            <Form className="row  d-flex justify-content-start mem_inf">
              <Form.Group controlId="formBasicEmail" className="mem_form">
                <Form.Label className="d-flex ">
                  <p className="subtitle1" style={{ color: '#df3d00' }}>
                    *
                  </p>
                  <p className="subtitle1">帳號</p>
                </Form.Label>
                {member.map((val) => {
                  return (
                    <Form.Control
                      type="text"
                      placeholder="帳號"
                      className="  text_w"
                      value={val.member_account}
                    />
                  )
                })}
              </Form.Group>
              <Form.Group controlId="formBasicEmail" className="mem_form">
                <Form.Label className="d-flex">
                  <p className="subtitle1" style={{ color: '#df3d00' }}>
                    *
                  </p>
                  <p className="subtitle1">名稱</p>
                </Form.Label>
                {member.map((val) => {
                  return (
                    <Form.Control
                      type="name"
                      placeholder=" 名稱"
                      className="  text_w"
                      value={val.member_name}
                    />
                  )
                })}
              </Form.Group>
              <Form.Group controlId="formBasicEmail" className="mem_form">
                <Form.Label className="d-flex">
                  <p className="subtitle1" style={{ color: '#df3d00' }}>
                    *
                  </p>
                  <p className="subtitle1">信箱</p>
                </Form.Label>

                {member.map((val) => {
                  return (
                    <Form.Control
                      type="email"
                      placeholder="信箱"
                      className="  text_w"
                      value={val.member_email}
                    />
                  )
                })}
              </Form.Group>

              <Form.Group>
                <Form.Label className="d-flex mem_form">
                  <p className="subtitle1" style={{ color: '#df3d00' }}>
                    *
                  </p>
                  <p className="subtitle1">性別</p>
                </Form.Label>
                {member.map((val) => {
                  return (
                    <Form.Label className="mem_form ">
                      <div className="d-flex text_w">
                        <Form.Check
                          type="radio"
                          label="男"
                          value={val.member_gender}
                          checked={({ gender } = '男') ? '男' : '女'}
                          name="
                    formHorizontalRadio"
                          id="formHorizontalRadios1"
                        />
                        <Form.Check
                          type="radio"
                          label="女"
                          value={val.member_gender}
                          name="
                    formHorizontalRadio"
                          id="formHorizontalRadios2"
                        />
                      </div>
                    </Form.Label>
                  )
                })}
              </Form.Group>

              <Form.Group controlId="formBasicEmail" className="mem_form">
                <Form.Label>生日</Form.Label>

                {member.map((val) => {
                  return (
                    <Form.Control
                      type="date"
                      placeholder=""
                      className="text_w"
                      value={val.member_birthday}
                    />
                  )
                })}
              </Form.Group>
              <Form.Group
                controlId="exampleForm.ControlSelect1"
                className="mem_form"
              >
                <Form.Label>攝影經驗</Form.Label>
                {member.map((val) => {
                  return (
                    <Form.Control
                      as="select"
                      className="  text_w"
                      value={val.member_ex}
                    >
                      <option className="subtitle1" value="0">
                        一年以下
                      </option>
                      <option className="subtitle1" value="1">
                        一年
                      </option>
                      <option className="subtitle1" value="3">
                        兩年
                      </option>
                      <option className="subtitle1" value="4">
                        四年
                      </option>
                      <option className="subtitle1" value="5">
                        五年
                      </option>
                      <option className="subtitle1" value="6">
                        六年
                      </option>
                      <option className="subtitle1" value="7">
                        七年
                      </option>
                      <option className="subtitle1" value="8">
                        七年以上
                      </option>
                    </Form.Control>
                  )
                })}
              </Form.Group>

              <Form.Group controlId="formBasicEmail" className="mem_form">
                <Form.Label>電話</Form.Label>
                {member.map((val) => {
                  return (
                    <Form.Control
                      type="photo"
                      placeholder="請輸入電話"
                      className="text_w"
                      value={val.member_phone}
                    />
                  )
                })}
              </Form.Group>
              <Form.Group controlId="formBasicEmail" className="mem_form">
                <Form.Label>關於我</Form.Label>
                {member.map((val) => {
                  return (
                    <Form.Control
                      as="textarea"
                      rows={2}
                      placeholder="關於我"
                      className="  text_w"
                      value={val.member_about}
                    />
                  )
                })}
              </Form.Group>
            </Form>
            <div className="d-flex justify-content-end">
              {member.map((val) => {
                return (
                  <Button
                    type="submit"
                    className="btn-style botton-font btn_icon "
                    onClick={() => {
                      UpdateReview(val.member_id)
                    }}
                  >
                    <MdDone />
                    儲存
                  </Button>
                )
              })}
            </div>
          </Card.Body>
        </Card>
      </div>
    </>
  )
}

export default withRouter(MemberContent)
