import '../../index.scss'
import React, { useState, useEffect } from 'react'
import MemberContent from '../../components/Member/MemberContent'
import MemberCard from '../../components/Member/MemberCard'
import MemberNavlist from '../../components/Member/MemberNavlist'
import '../../style/member/member.scss'
import { devUrl } from '../../config'
function MemberHome(props) {
  const [member_name, setMembername] = useState('')
  const [member_account, setMemberaccount] = useState('')
  const [member, setMember] = useState([])
  const [newaccount, setNewMemberaccount] = useState('')

  useEffect(() => {
    Axios.get('http://localhost:3001/api/get').then((res) => {
      setMember(res.data)
    })
  })
  const submitReview = () => {
    Axios.post('http://localhost:3001/api/insert', {
      member_name: member_name,
      member_account: member_account,
    }).then(() => {
      setMember([
        ...member,
        { member_name: member_name, member_account: member_account },
      ])
    })
  }
  const deleteReview = (member) => {
    Axios.delete(`http://localhost:3001/api/delete/${member}`)
  }
  return (
    <>
      <body>
        <div className="background_wave2 ">
          <div className="wrapper  ">
            <main className="container">
              <aside className="d-flex justify-content-end">
                <br />
                <div>
                  <MemberCard />
                  <br />
                  <MemberNavlist />
                </div>

                <article>
                  <MemberContent />
                </article>
              </aside>
            </main>
          </div>
          <br />
        </div>
      </body>
    </>
  )
}

export default MemberHome
