import React from 'react'
import '../../style/default.scss'
import '../../style/event/event_album.scss'
import { devUrl } from '../../config'
import { MdKeyboardArrowLeft } from 'react-icons/md'
import { Link, withRouter } from 'react-router-dom'
import Axios from 'axios'

function SeeMemberAlbum() {
  const [memberevent, setMemberEvent] = useState([])
  const [events, setEvents] = useState([])
  const [att, setAtt] = useState([])
  const [photo, setPhoto] = useState([])
  const [attphoto, setAttphoto] = useState([])
  const [addphoto, setAddphoto] = useState([])

  const getEvent = async () => {
    await Axios.get(
      `http://localhost:3001/member/get/history/event/${props.match.params.id}`
    )
      .then((res) => {
        setMemberEvent(res.data[0])
        // console.log(res.data[0])
        if (res.data) {
          console.log(JSON.parse(res.data[0].event_id))
          setEvents(JSON.parse(res.data[0].event_id))
        } else {
          return
        }
      })
      .catch(function (error) {
        console.log(error)
      })
  }

  const getAtt = async () => {
    await Axios.get(
      `http://localhost:3001/member/history/event/att?id=${events.join(',')}`
    )
      .then((res) => {
        setAtt(res.data)
        console.log(res.data)
      })
      .catch(function (error) {
        console.log(error)
      })
  }

  const getPhoto = async () => {
    await Axios.get(
      `http://localhost:3001/member/get/event/photo?id=${events.join(
        ','
      )}&member=${props.match.params.id}`
    )
      .then((res) => {
        setPhoto(res.data)
        console.log(res.data)
      })
      .catch(function (error) {
        console.log(error)
      })
  }

  useEffect(() => {
    getEvent()
  }, [])

  useEffect(() => {
    if (events.length > 0) {
      getAtt()
      getPhoto()
    }
  }, [events])
  return (
    <>
      <div className="album-page">
        <div className="go-back d-flex align-items-center">
          <MdKeyboardArrowLeft />
          <Link to="/see">
            <div>回到會員頁</div>
          </Link>
        </div>

        <div className="title row">
          {att.map((m) => {
            return (
              <div className="word col-9">
                <h6>2020/03/05-2020/03/17</h6>
                <h5>{m.event_name}</h5>
              </div>
            )
          })}
        </div>

        {/* 圖片部分開始 */}
        <div className="album-slider d-flex justify-content-center">
          <div className="album-content row">
            <a href={devUrl + '/see/Album/photo'} className="photo-card col-4">
              <figure>
                <img src="" alt=""></img>
              </figure>
              <div>
                <h6>我是標題</h6>
              </div>
            </a>

            <div className="photo-card col-4">
              <figure>
                <img src="" alt=""></img>
              </figure>
              <div>
                <h6>我是標題</h6>
              </div>
            </div>
            <div className="photo-card col-4">
              <figure>
                <img src="" alt=""></img>
              </figure>
              <div>
                <h6>我是標題</h6>
              </div>
            </div>
            <div className="photo-card col-4">
              <figure>
                <img src="" alt=""></img>
              </figure>
              <div>
                <h6>我是標題</h6>
              </div>
            </div>
            <div className="photo-card col-4">
              <figure>
                <img src="" alt=""></img>
              </figure>
              <div>
                <h6>我是標題</h6>
              </div>
            </div>
            <div className="photo-card col-4">
              <figure>
                <img src="" alt=""></img>
              </figure>
              <div>
                <h6>我是標題</h6>
              </div>
            </div>
            <div className="photo-card col-4">
              <figure>
                <img src="" alt=""></img>
              </figure>
              <div>
                <h6>我是標題</h6>
              </div>
            </div>
            <div className="photo-card col-4">
              <figure>
                <img src="" alt=""></img>
              </figure>
              <div>
                <h6>我是標題</h6>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  )
}
export default withRouter(SeeMemberAlbum)
